import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import './BuyActionWindow.css';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:3002/orders");
        setOrders(res.data || []);
      } catch (error) {
        console.error("Failed to fetch orders", error);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  // Loading state
  if (loading) {
    return (
      <div className="orders">
        <div className="no-orders">
          <p>Loading orders...</p>
        </div>
      </div>
    );
  }

  // No orders
  if (orders.length === 0) {
    return (
      <div className="orders">
        <div className="no-orders">
          <p>You haven't placed any orders today</p>
          <Link to="/" className="btn">
            Get started
          </Link>
        </div>
      </div>
    );
  }

  // Orders available
  return (
    <div className="orders">
      <h3 className="title">Orders</h3>

      <div className="order-table">
        <table>
          <thead>
            <tr>
              <th>Instrument</th>
              <th>Qty</th>
              <th>Price</th>
              <th>Type</th>
              <th>Status</th>
            </tr>
          </thead>

          <tbody>
            {orders.map((order, index) => (
              <tr key={index}>
                <td className="align-left">{order.name}</td>
                <td>{order.qty}</td>
                <td>â‚¹{order.price}</td>
                <td><div className={order.mode === "BUY" ? "btn-blue": "btn-red"} style={{width: "10%"}} >{order.mode}</div></td>
                <td>
                  <span
                    className={
                      order.mode === "BUY" ? "profit" : "loss"
                    }
                  >
                    Completed
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Orders;
